.cta-button {
  background-color: #ff6b35;
  color: white;
  font-size: 18px;
  padding: 15px 30px;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.cta-button:hover {
  background-color: #e55a2b;
  transform: translateY(-2px);
}

/* گەرماکەرەوەی بۆ ڕەنگەکان */
:root {
  --primary-color: #2c3e50;
  --accent-color: #3498db;
  --success-color: #27ae60;
  --warning-color: #f39c12;
}

/* تایپۆگرافیای کوردی */
body {
  font-family: 'Noto Sans Arabic', sans-serif;
  line-height: 1.6;
  direction: rtl;
}

// نموونەی AI-powered personalization
const userPreferences = {
  theme: 'dark',
  language: 'ku',
  accessibility: {
    fontSize: 'large',
    contrast: 'high'
  }
};

function personalizeInterface(user) {
  return {
    theme: user.preferences.theme,
    content: getPersonalizedContent(user.id),
    layout: adaptLayout(user.accessibility)
  };
}

/* نموونەی CSS بۆ Dark Mode */
:root {
  --background: #ffffff;
  --text: #000000;
}

[data-theme="dark"] {
  --background: #1a1a1a;
  --text: #ffffff;
}

.container {
  background-color: var(--background);
  color: var(--text);
  transition: all 0.3s ease;
}

// نموونەی Server Component
async function BlogPost({ id }) {
  const post = await fetchPost(id);

  return (
    <article>
      <h1>{post.title}</h1>
      <p>{post.content}</p>
    </article>
  );
}

// نموونەی کۆدی Rust بۆ WebAssembly
#[wasm_bindgen]
pub fn calculate(a: i32, b: i32) -> i32 {
    a + b
}
